import React , {useReducer} from "react";
import CartContext from "./cart-context";

const defaultState = {
    items: [],
    totalAmount: 0,
};

const cartReducer = (state,action) => {
    if(action.tpye === 'ADD'){

        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;


        // const existingCartItemIndex = state.items.findIndex( item => item.id === state.item.id);
        // let isExistingCartItemBool;
        // let existingCartItem;

        // if(existingCartItemIndex === -1){
        //     isExistingCartItemBool = false;
        // }else{
        //     isExistingCartItemBool = true;
        //      existingCartItem = state.items[existingCartItemIndex];
        // }
        // let updatedItems;

        // if(isExistingCartItemBool){
        //     const updateItem = {
        //         ...existingCartItem , 
        //         amount: existingCartItem.amount + action.item.amount
        //     };
        //     updateItem = [...state.items];
        //     updatedItems[existingCartItemIndex] = updateItem;

        // }else{
        //     const updatedItems = state.items.concat(action.item);
        // }

        return {
            items: updatedItems,
            totalAmount: updatedTotalAmount
        }
    }
    return defaultState;
}

const CartProvider = (props) => {

    const [cartState,dispatchCartAction ] = useReducer(cartReducer, defaultState)


    const addItemToCartHandler = (item)=> {
        dispatchCartAction({tpye: 'ADD', item: item})
    }

    const removeItemFromCartHandler = (id)=> {
        dispatchCartAction({tpye: 'REMOVE', id: id})
    }


    const cartContext = {
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemToCartHandler ,
        removeItem: removeItemFromCartHandler,
    };


    return <CartContext.Provider value={cartContext}>
        {props.children}
    </CartContext.Provider>
}

export default CartProvider;
